import React, {useRef} from 'react';
import {Route, Routes} from "react-router-dom";
import SignIn from "../pages/signIn";
import SignUp from "../pages/signUp";
import {Player} from "@lottiefiles/react-lottie-player";
import ToggleImage from '../assets/json/toggle.json';
import {nowTheme} from "../utils/util";

const Main = () => {
    const toggleRef = useRef<any>();

    const play = () => {
        nowTheme();
        // toggleRef.current.setPlayerDirection(1);
        toggleRef.current.play(true);
    }

    return (
        <main className="w-full h-screen flex justify-center items-center bg-white dark:bg-[#1b1b1b]">
            <div onMouseOver={() => play()}>
                <Player
                    src={ToggleImage}
                    autoplay={true}
                    loop={false}
                    keepLastFrame={false}
                    renderer={'svg'}
                    ref={toggleRef}
                    direction={-1}
                />
            </div>
            <img src="" alt=""/>
            <Routes>
                <Route path="/" element={<SignIn />} />
                <Route path="/signup" element={<SignUp />} />
            </Routes>
        </main>
    );
};

export default Main;
