import React, {useState} from 'react';
import './App.css';
import {useAppDispatch, useAppSelector} from './redux/store';
import {AuthService, UserData} from "./services/authService";
import {signOut} from "./redux/slices/signInSlice";
import {DummyService} from "./services/dummyService";
import {removeEmployee} from "./redux/slices/employeeSlice";

function App() {
  const [userData, setUserData] = useState<UserData>({ userId: '', password: '' });
  const [employerId, setEmployerId] = useState(0);
  const dispatch = useAppDispatch();
  const { loading, error, data } = useAppSelector(state => state.authSlice);
  const employeeReducer = useAppSelector(state => state.employeeSlice);

  const signIn = async () => {
    dispatch(AuthService.signIn(userData));
  }

  const handleClick = () => {
    dispatch(DummyService.getEmployer(employerId))
  }

  return (
    <div className="App">
      <input
          type="text"
          value={userData.userId}
          onChange={(e) => setUserData({ ...userData, userId: e.target.value })}
      />
      <input
          type="text"
          value={userData.password}
          onChange={(e) => setUserData({ ...userData, password: e.target.value })}
      />
      <button onClick={signIn}>Sign In</button>
      <button onClick={() => dispatch(signOut())}>Sign out</button>
      <div>{loading ? 'loading' : null}</div>
      <div>{data ? data.userId : null}</div>
      <div>{error ? error : null}</div>

        <div>
            <button onClick={() => dispatch(DummyService.getEmployers())}>get Employers</button>
            <div>{employeeReducer.isLoading ? 'loading' : null}</div>
            {employeeReducer.employers ? employeeReducer.employers.map(employee => (
                <div
                    onClick={() => dispatch(removeEmployee(employee.employee_name))}
                    key={employee.id}
                >{employee.employee_name}</div>)
            ) : null}
            <div>{employeeReducer.error ? employeeReducer.error : null}</div>
        </div>

        <div>
            <input type="number" value={employerId} onChange={(e) => setEmployerId(e.target.value)} />
            <button onClick={handleClick}>get Employer</button>
            <div>{employeeReducer.isLoading ? 'loading' : null}</div>
            {employeeReducer.employers ? employeeReducer.employers.map(employee => (
                <div
                    onClick={() => dispatch(removeEmployee(employee.employee_name))}
                    key={employee.id}
                >{employee.employee_name}</div>)
            ) : null}
            <div>{employeeReducer.error ? employeeReducer.error : null}</div>
        </div>

    </div>
  );
}

export default App;
