import React, {useEffect, useState} from 'react';
import {signOut} from "../redux/slices/signInSlice";
import {AuthService, loginData} from "../services/authService";
import {useAppDispatch, useAppSelector} from "../redux/store";

const SignIn = () => {
    const [loginData, setLoginData] = useState<loginData>({ email: '', password: '' });
    const dispatch = useAppDispatch();
    const { loading, error, data } = useAppSelector(state => state.authSlice);

    const signIn = async () => {
        dispatch(AuthService.signIn(loginData));
    }

    useEffect(() => {
        if (!data.id && loading === false) {
            setLoginData({ email: '', password: '' })
        }
    }, [data])

  return (
    <section className="sm:w-[400px] w-[250px] h-1/2 flex flex-col justify-center items-center border border-solid drop-shadow-lg	shadow-[#195dae] p-5">
      <div className="w-full">
        <div className=" flex justify-center items-center pb-2">
          <label className="w-[130px]" htmlFor="email">EMAIL</label>
          <input
            id="email"
            type="email"
            value={loginData.email}
            onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
            className="w-full text-sm border border-solid border-[#1B1B1B] p-1 focus:border-[#195dae]"
          />
        </div>
        <div className=" flex justify-center items-center">
          <label className="w-[130px]" htmlFor="email">PASSWORD</label>
          <input
            type="password"
            value={loginData.password}
            onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
            className="w-full text-sm border border-solid border-[#1B1B1B] p-1 focus:border-[#195dae]"
          />
        </div>
          <button onClick={signIn} className="w-full h-10 text-[#195dae] border border-solid border-[#195dae] rounded-xl duration-100 mt-5 hover:bg-[#195dae] hover:text-white">Sign In</button>
      </div>
        {/*<button onClick={() => dispatch(signOut())}>Sign out</button>*/}
        {/*<div>{loading ? 'loading' : null}</div>*/}
        {/*<div>{data ? data.username : null}</div>*/}
        {/*<div>{error ? error : null}</div>*/}
    </section>
  );
};

export default SignIn;
