import React, {useEffect, useState} from 'react';
import {signOut} from "../redux/slices/signInSlice";
import {AuthService, loginData} from "../services/authService";
import {useAppDispatch, useAppSelector} from "../redux/store";

const SignIn = () => {
    const [loginData, setLoginData] = useState<loginData>({ email: '', password: '' });
    const dispatch = useAppDispatch();
    const { loading, error, data } = useAppSelector(state => state.authSlice);

    const signIn = async () => {
        dispatch(AuthService.signIn(loginData));
    }

    useEffect(() => {
        if (!data.id && loading === false) {
            setLoginData({ email: '', password: '' })
        }
    }, [data])

    return (
        <section className="sm:w-[400px] w-[250px] h-1/2 border border-solid border-[#195dae]">
            <input
                type="text"
                value={loginData.email}
                onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                className="border border-solid border-[#1B1B1B]"
            />
            <input
                type="text"
                value={loginData.password}
                onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
            />
            <button onClick={signIn}>Sign In</button>
            <button onClick={() => dispatch(signOut())}>Sign out</button>
            <div>{loading ? 'loading' : null}</div>
            <div>{data ? data.username : null}</div>
            <div>{error ? error : null}</div>
        </section>
    );
};

export default SignIn;
