import {createSlice} from "@reduxjs/toolkit";
import {DummyService} from "../../services/dummyService";

interface Employer {
    id: string,
    employee_name: string,
    employee_salary: string,
    employee_age: string,
    profile_image: string
}

type EmployeeData = {
    isLoading: boolean,
    error: string | undefined,
    employers: Employer[]
}

const initialState: EmployeeData = {
    isLoading: false,
    error: '',
    employers: [{
        id: '',
        employee_name: '',
        employee_salary: '',
        employee_age: '',
        profile_image: ''
    }],
}

export const employeeSlice = createSlice({
    name: 'EMPLOYEE',
    initialState,
    reducers: {
        removeEmployee(state, action) {
            const newEmployers = state.employers.filter(employer => employer !== action.payload);
            state.employers = newEmployers;
        }
    },
    extraReducers: (builder) => {
        builder
            .addCase(DummyService.getEmployee.pending, (state) => {
                state.isLoading = true;
                state.employers = initialState.employers;
            })
            .addCase(DummyService.getEmployee.fulfilled, (state, action) => {
                state.isLoading = false;
                console.log(action.payload)
                state.employers = action.payload;
            })
            .addCase(DummyService.getEmployee.rejected, (state,action) => {
                state.isLoading = false;
                state.employers = initialState.employers;
                state.error = action.payload as string;
            })
    }
});
