import {createSlice} from "@reduxjs/toolkit";
import {nowTheme} from "../../utils/util";
import {authSlice} from "./signInSlice";

const initialState = 'light';

export const themeSlice = createSlice({
  name: 'THEME',
  initialState,
  reducers: {
    getTheme(state) {
      const theme = nowTheme();
      console.log(theme, 'bnbb')
      state = theme;
    }
  }
})

export const { getTheme } = themeSlice.actions;
