import {createSlice} from "@reduxjs/toolkit";
import {DummyService} from "../../services/dummyService";

interface Employer {
    id: string,
    employee_name: string,
    employee_salary: string,
    employee_age: string,
    profile_image: string
}

type EmployeeData = {
    isLoading: boolean,
    error: string | undefined,
    employer: Employer
}

const initialState: EmployeeData = {
    isLoading: false,
    error: '',
    employer: {
        id: '',
        employee_name: '',
        employee_salary: '',
        employee_age: '',
        profile_image: ''
    },
}

export const employeeSlice = createSlice({
    name: 'EMPLOYEE',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder
            .addCase(DummyService.getEmployee.pending, (state) => {
                state.isLoading = true;
                state.employer = initialState.employer;
            })
            .addCase(DummyService.getEmployee.fulfilled, (state, action) => {
                state.isLoading = false;
                state.employer = action.payload;
            })
            .addCase(DummyService.getEmployee.rejected, (state,action) => {
                state.isLoading = false;
                state.employer = initialState.employer;
                state.error = action.payload as string;
            })
    }
});
